function form=trh(vis)%helpstr=help('trebdsgn') fig = findobj(get(0,'children'),'flat','tag','trebhelp'); if length(fig) > 0 &nargin~=1	set(fig,'visible','on'); 	figure(fig); 	return; elseif length(fig) == 0 	A=imread('treb.jpg');%name of graphics file here%load trebpic;%uncomment thie line if the graphic has %                        been saved in the file trebpic in the %                        variable named 'A'graphicwidth=400;%enter correct graphic width here graphicheight=200;helpstr=help('trhelp') helpstr=cellstr(helpstr);HelpDlgWidth=500;HelpDlgHeight=440;set(0,'units','pixels');ScreenSize=get(0,'screensize');ScreenWidth=ScreenSize(3);ScreenHeight=ScreenSize(4);HelpFigHandle=figure('visible','off','tag','trebhelp');set(gcf,'units','pixels')set(HelpFigHandle,'position',[ScreenWidth/2-HelpDlgWidth/2 ...                  ScreenHeight-(ScreenHeight-HelpDlgHeight)/2-HelpDlgHeight ...				  HelpDlgWidth HelpDlgHeight],'resize','off')				  OKWidth=30;OKHeight=15;OKBottomSep=5;OKHandle=uicontrol('Style'              ,'pushbutton'                      , ...                  'Units'              ,'pixels'                          , ...                 'Position'           ,[ HelpDlgWidth/2-OKWidth/2 ...			                             OKBottomSep OKWidth   OKHeight]  , ...                  'String'             ,'OK'                              , ...                  'HorizontalAlignment','center'                          , ...				  'callback','set(gcf,''visible'',''off'')',...                  'Tag'                ,'OKButton'                          ...                  );%                  'CallBack'           ,'delete(get(0,''CurrentFigure''))', ...                                   MsgTxtWidth=450;	MsgTxtHeight=210;MsgTxtBackClr=[1 1 1];MsgTxtForeClr=[0 0 0];				  MsgHandle=uicontrol('Style'              ,'listbox'         , ...                   'Units'              ,'pixels'       , ...                   'Position'           ,[HelpDlgWidth/2-MsgTxtWidth/2 ...                                          OKHeight+2*OKBottomSep ...                                          MsgTxtWidth   ...                                          MsgTxtHeight  ...                                         ]              , ...                   'String'             ,helpstr            , ...                   'Tag'                ,'MessageBox'   , ...                   'HorizontalAlignment','left'         , ...                       'BackgroundColor'    ,MsgTxtBackClr  , ...                   'FontWeight'         ,'bold'         , ...                   'ForegroundColor'    ,MsgTxtForeClr    ...                   );[helpstr,NewMsgTxtPos]=textwap(MsgHandle,helpstr,75);NumLines=size(helpstr,1);%MsgTxtWidth=max(MsgTxtWidth,NewMsgTxtPos(3));%MsgTxtHeight=max(MsgTxtHeight,NewMsgTxtPos(4));set(MsgHandle, ...   'Max'     ,NumLines                                              , ...   'Position'           ,[HelpDlgWidth/2-MsgTxtWidth/2 ...                          OKHeight+2*OKBottomSep ...                          MsgTxtWidth   ...                          MsgTxtHeight  ...                         ]               ...   );set(MsgHandle,'String',helpstr)%A=imread('SLATER10.JPG');%set(gcf,'units','pixels')axes('units','pixels','position',...[1+OKBottomSep OKHeight+OKBottomSep*3+MsgTxtHeight HelpDlgWidth-OKBottomSep... HelpDlgHeight-MsgTxtHeight-OKHeight-OKBottomSep*7])image(A)		set(gca,'visible','off')zoom ona=title('Click once to zoom in, shift click to zoom out');set(a,'visible','on')endif nargin==0	set(gcf,'visible','on')end